sprites.onOverlap(SpriteKind.Player, SpriteKind.gunshopcounter, function (sprite, otherSprite) {
    game.splash("Welcome to the gunshop!")
    if (game.ask("can i help you?")) {
        if (game.ask("change bullet type?")) {
            if (game.ask("equip regular bullets?")) {
                using_fire_bullets = false
            } else if (has_fire_bullets) {
                if (game.ask("equip fire bullets?")) {
                    using_fire_bullets = true
                } else {
                    game.splash("goodbye!")
                }
            } else {
                game.splash("goodbye!")
            }
        } else if (game.ask("change gun?")) {
            if (hasGun) {
                if (game.ask("equip pistol?")) {
                    equipped_pistol = true
                    using_shotgun = false
                } else if (has_shotgun) {
                    if (game.ask("equip shotgun?")) {
                        using_shotgun = true
                        equipped_pistol = false
                    }
                } else {
                    game.splash("goodbye! n")
                }
            } else {
                game.splash("goodbye! ")
            }
        } else {
            game.splash("goodbye! ")
        }
    } else {
        game.splash("goodbye!")
    }
    mySprite.setPosition(mySprite.x, 50024)
})
